function showModalAddingWindow(){add(counter),modalAddingWindow.classList.add("modal-show")}function add(o){counter=1;var t=Number(purchaseCartCounter.innerHTML),e=counter+t;purchaseCartCounter.innerHTML=e,purchaseCart.style.backgroundColor="#EE3643"}const modalAddingWindow=document.querySelector(".modal-adding-window"),itemsButtons=document.querySelectorAll(".catalog-page__item-button, .catalog-page__purchase-button, .popular-products__item-button, .popular-products__purchase-button"),closeButton=modalAddingWindow.querySelector(".modal-adding-window__close-button"),continueButton=modalAddingWindow.querySelector(".modal-adding-window__continue-button"),purchaseCartCounter=document.querySelector(".header__purchase-cart-counter"),purchaseCart=document.querySelector(".header__purchase-cart");for(var counter=0,i=0;i<=itemsButtons.length-1;i++)itemsButtons[i].addEventListener("click",function(o){o.preventDefault(),showModalAddingWindow()});closeButton.addEventListener("click",function(){modalAddingWindow.classList.remove("modal-show")}),continueButton.addEventListener("click",function(){modalAddingWindow.classList.remove("modal-show")});